mapper.select:
select m.MEMBER_ID, m.USERNAME, m.NICKNAME, m.FULLNAME, m.PHONE, m.SEX, m.PHOTO, m.WAITACCOUNT, m.ALREADYACCOUNT, m.WXOPEN_ID, m.FROMWXOPEN_ID, m.MEMBERTYPE_ID, m.CLASS, m.MARKET_ID, mt.MEMBERTYPENAME, mk.MARKETNAME, dn.DOMAINNAME from m left join membertype mt on m.membertype_id = mt.membertype_id left join market mk on m.market_id = mk.market_id left join domain dn on mk.domain_id = dn.domain_id where 1 = 1
and ( m.USERNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.NICKNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.FULLNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.PHONE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and m.MEMBERTYPE_ID = #{pd.MEMBERTYPE_ID}
and m.FROMWXOPEN_ID = #{pd.FROMWXOPEN_ID}
and m.CLASS = #{pd.CLASS}
and dn.domain_id = #{pd.DOMAIN_ID}
and mk.market_id = #{pd.MARKET_ID}
order by m.MEMBER_ID ASC

select m.MEMBER_ID, m.USERNAME, m.NICKNAME, m.FULLNAME, m.PHONE, m.SEX, m.PHOTO, m.WAITACCOUNT, m.ALREADYACCOUNT, m.WXOPEN_ID, m.FROMWXOPEN_ID, m.MEMBERTYPE_ID, m.CLASS, m.MARKET_ID, mt.MEMBERTYPENAME, mk.MARKETNAME, dn.DOMAINNAME from m left join membertype mt on m.membertype_id = mt.membertype_id left join market mk on m.market_id = mk.market_id left join domain dn on mk.domain_id = dn.domain_id where 1 = 1
and ( m.USERNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.NICKNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.FULLNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.PHONE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and m.MEMBERTYPE_ID = #{MEMBERTYPE_ID}
and m.FROMWXOPEN_ID = #{FROMWXOPEN_ID}
and m.CLASS = #{CLASS}
and dn.domain_id = #{DOMAIN_ID}
and mk.market_id = #{MARKET_ID}
order by m.MEMBER_ID ASC

select m.MEMBER_ID, m.USERNAME, m.NICKNAME, m.FULLNAME, m.PHONE, m.SEX, m.PHOTO, m.WAITACCOUNT, m.ALREADYACCOUNT, m.WXOPEN_ID, m.FROMWXOPEN_ID, m.MEMBERTYPE_ID, m.CLASS, m.MARKET_ID, mt.MEMBERTYPENAME from m left join membertype mt on m.membertype_id = mt.membertype_id where 1 = 1
and m.MARKET_ID = #{MARKET_ID}
and m.CLASS = #{CLASS}
order by m.WAITACCOUNT DESC LIMIT 0,100

select m.MEMBER_ID, m.USERNAME, m.NICKNAME, m.FULLNAME, m.PHONE, m.SEX, m.PHOTO, m.WAITACCOUNT, m.ALREADYACCOUNT, m.WXOPEN_ID, m.FROMWXOPEN_ID, m.MEMBERTYPE_ID, m.CLASS, m.MARKET_ID, mt.MEMBERTYPENAME, ifnull(t.COUNT,0) COUNT from m left join membertype mt on m.membertype_id = mt.membertype_id left join ( select m.member_id ,count(1) count from ((select m1.member_id from member m1 left join member m2 on m1.wxopen_id = m2.fromwxopen_id where m2.wxopen_id is not null ) UNION all (select m1.member_id from member m1 left join member m2 on m1.wxopen_id = m2.fromwxopen_id left join member m3 on m2.wxopen_id = m3.fromwxopen_id where m3.wxopen_id is not null ) ) m group by m.member_id ) t on m.member_id = t.member_id
where 1 = 1
and m.MARKET_ID = #{MARKET_ID}
and m.CLASS = #{CLASS}
order by t.COUNT DESC
LIMIT 0,100

     	select
     		ORDER_ID, 
     		ORDERSN,
     		CDT
     	from orders where order_id in (
     		select order_id from account where type in (2,3,4) and member_id in (
				select member_id from member m where m.wxopen_id = #{WXOPEN_ID}
				UNION all 
				select member_id from member m where m.fromwxopen_id = #{WXOPEN_ID}
				UNION all select member_id from member m where m.fromwxopen_id in (select wxopen_id from member m where m.fromwxopen_id = #{WXOPEN_ID})
			) group by order_id
     	)
		order by cdt desc

select m.MEMBER_ID, m.USERNAME, m.NICKNAME, m.FULLNAME, m.PHONE, m.SEX, m.PHOTO, m.WAITACCOUNT, m.ALREADYACCOUNT, m.WXOPEN_ID, m.FROMWXOPEN_ID, m.MEMBERTYPE_ID, m.CLASS, m.MARKET_ID, mt.MEMBERTYPENAME from m left join membertype mt on m.membertype_id = mt.membertype_id where 1 = 1
AND m.WXOPEN_ID = #{WXOPEN_ID}
limit 0,1

select m.MEMBER_ID, m.USERNAME, m.NICKNAME, m.FULLNAME, m.PHONE, m.SEX, m.PHOTO, m.WAITACCOUNT, m.ALREADYACCOUNT, m.WXOPEN_ID, m.FROMWXOPEN_ID, m.MEMBERTYPE_ID, m.CLASS, m.MARKET_ID, mt.MEMBERTYPENAME, IFNULL((select sum(givemoney) memoney from account where member_id = m.member_id and type=2),0) AS MEACCOUNT, IFNULL((select sum(givemoney) from account where type in (3,4) and member_id = m.member_id),0) AS TEAMACCOUNT from m left join membertype mt on m.membertype_id = mt.membertype_id where 1 = 1 and m.membertype_id in (3,4)
and ( m.USERNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.NICKNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.FULLNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') OR m.PHONE LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and m.MEMBERTYPE_ID = #{pd.MEMBERTYPE_ID}
and m.FROMWXOPEN_ID = #{pd.FROMWXOPEN_ID}
and m.market_id = #{pd.MARKET_ID}
order by m.MEMBER_ID ASC
