mapper.select:
select os.ORDER_ID, os.ORDERSN, os.WEIXINSN, os.MEMBER_ID, os.ORIGINAL, os.MONEY, os.DERATE, os.CDT, os.UDT, os.USEPERSON, os.PERSONPHONE, os.STATE, osi.GOODS_ID, osi.MONEY AS MONEYONE, osi.NUMBER, g.GOODSNAME, g.GOODSDESC, s.SHOP_ID, p.FILEPATH from os left join ordersitem osi on os.order_id = osi.order_id left join goods g on osi.goods_id = g.goods_id left join shop s on g.shop_id = s.shop_id left join ( SELECT * FROM file WHERE filetype = '1' GROUP BY reference_id ) p on g.goods_id = p.reference_id where 1 = 1
and ( os.ORDERSN LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and os.MEMBER_ID = #{pd.MEMBER_ID}
and os.STATE = #{pd.STATE}
and s.SHOP_ID = #{pd.SHOP_ID}
${pd.SQLCONDITION}
order by os.ORDER_ID DESC

select os.ORDER_ID, os.ORDERSN, os.WEIXINSN, os.MEMBER_ID, os.ORIGINAL, os.MONEY, os.DERATE, os.CDT, os.UDT, os.USEPERSON, os.PERSONPHONE, os.STATE, osi.GOODS_ID, osi.MONEY AS MONEYONE, osi.NUMBER, g.GOODSNAME, g.GOODSDESC, s.SHOP_ID, p.FILEPATH from os left join ordersitem osi on os.order_id = osi.order_id left join goods g on osi.goods_id = g.goods_id left join shop s on g.shop_id = s.shop_id left join ( SELECT * FROM file WHERE filetype = '1' GROUP BY reference_id ) p on g.goods_id = p.reference_id where 1 = 1
and ( os.ORDERSN LIKE CONCAT(CONCAT('%', #{keywords}),'%') )
and os.MEMBER_ID = #{MEMBER_ID}
and os.STATE = #{STATE}
and s.SHOP_ID = #{SHOP_ID}
${SQLCONDITION}
order by os.ORDER_ID DESC
