mapper.select:
select s.SHOP_ID, s.MEMBER_ID, s.SHOPNAME, s.SHOPDESC, s.SHOPADDRESS, s.AVGMONEY, s.LONGITUDE, s.LATITUDE, s.CONTRACTPERSON, s.CONTRACTPHONE, s.ACCOUNTER, s.CDT, s.SHOPSTATE_ID, s.SHOPTYPE_ID, s.DISTANCE, s.WAITACCOUNT, s.ALREADYACCOUNT, s.MARKET_ID, s.WXFLAG, s.DYFLAG, s.MEMBER_ID_DY, s.PERCENT, ss.SHOPSTATENAME, st.SHOPTYPENAME, ifnull(f.FILEPATH,'shop.jpg') as FILEPATH, m.NICKNAME, m.PHOTO, m.WXOPEN_ID, mk.MARKETNAME from ( SELECT SHOP_ID, MEMBER_ID, SHOPNAME, SHOPDESC, SHOPADDRESS, AVGMONEY, LONGITUDE, LATITUDE, CONTRACTPERSON, CONTRACTPHONE, ACCOUNTER, CDT, SHOPSTATE_ID, SHOPTYPE_ID, WAITACCOUNT, ALREADYACCOUNT, MARKET_ID, WXFLAG, DYFLAG, MEMBER_ID_DY, PERCENT, ROUND( 6378.138 * 2 * ASIN( SQRT( POW( SIN( (
latitude
#{pd.LATITUDE}
* PI() / 180 - latitude * PI() / 180 ) / 2 ), 2 ) + COS(
latitude
#{pd.LATITUDE}
* PI() / 180) * COS(latitude * PI() / 180) * POW( SIN( (
longitude
#{pd.LONGITUDE}
* PI() / 180 - longitude * PI() / 180 ) / 2 ), 2 ) ) ) * 1000 ) AS DISTANCE FROM shop ) s left join shopstate ss on s.shopstate_id = ss.shopstate_id left join shoptype st on s.shoptype_id = st.shoptype_id left join file f on s.shop_id = f.reference_id and f.filetype = '7' left join member m on s.member_id = m.member_id left join market mk on s.market_id = mk.market_id where 1 = 1
and ( s.SHOPNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and mk.domain_id = #{pd.DOMAIN_ID}
and s.MEMBER_ID = #{pd.MEMBER_ID}
and s.MEMBER_ID_DY = #{pd.MEMBER_ID_DY}
and s.SHOPSTATE_ID = #{pd.SHOPSTATE_ID}
and s.SHOPTYPE_ID = #{pd.SHOPTYPE_ID}
and s.MARKET_ID = #{pd.MARKET_ID}
and s.WXFLAG = #{pd.WXFLAG}
and s.DYFLAG = #{pd.DYFLAG}
${pd.SQLCONDITION}
order by s.SHOP_ID DESC

select s.SHOP_ID, s.MEMBER_ID, s.SHOPNAME, s.SHOPDESC, s.SHOPADDRESS, s.AVGMONEY, s.LONGITUDE, s.LATITUDE, s.CONTRACTPERSON, s.CONTRACTPHONE, s.ACCOUNTER, s.CDT, s.SHOPSTATE_ID, s.SHOPTYPE_ID, s.WAITACCOUNT, s.ALREADYACCOUNT, s.MARKET_ID, s.WXFLAG, s.DYFLAG, s.MEMBER_ID_DY, s.PERCENT, s.DISTANCE, ss.SHOPSTATENAME, st.SHOPTYPENAME, ifnull(f.FILEPATH,'shop.jpg') as FILEPATH, m.NICKNAME, m.PHOTO, m.WXOPEN_ID from ( SELECT SHOP_ID, MEMBER_ID, SHOPNAME, SHOPDESC, SHOPADDRESS, AVGMONEY, LONGITUDE, LATITUDE, CONTRACTPERSON, CONTRACTPHONE, ACCOUNTER, CDT, SHOPSTATE_ID, SHOPTYPE_ID, WAITACCOUNT, ALREADYACCOUNT, MARKET_ID, WXFLAG, DYFLAG, MEMBER_ID_DY, PERCENT, ROUND( 6378.138 * 2 * ASIN( SQRT( POW( SIN( (
latitude
#{LATITUDE}
* PI() / 180 - latitude * PI() / 180 ) / 2 ), 2 ) + COS(
latitude
#{LATITUDE}
* PI() / 180) * COS(latitude * PI() / 180) * POW( SIN( (
longitude
#{LONGITUDE}
* PI() / 180 - longitude * PI() / 180 ) / 2 ), 2 ) ) ) * 1000 ) AS DISTANCE FROM shop ) s left join shopstate ss on s.shopstate_id = ss.shopstate_id left join shoptype st on s.shoptype_id = st.shoptype_id left join file f on s.shop_id = f.reference_id and f.filetype = '7' left join member m on s.member_id = m.member_id left join market mk on s.market_id = mk.market_id where 1 = 1
and ( s.SHOPNAME LIKE CONCAT(CONCAT('%', #{keywords}),'%') )
and mk.domain_id = #{DOMAIN_ID}
and ( s.cdt LIKE CONCAT(CONCAT('%', #{CCDT}),'%') )
and s.MEMBER_ID = #{MEMBER_ID}
and s.MEMBER_ID_DY = #{MEMBER_ID_DY}
and s.SHOPSTATE_ID = #{SHOPSTATE_ID}
and s.SHOPTYPE_ID = #{SHOPTYPE_ID}
and s.MARKET_ID = #{MARKET_ID}
and s.WXFLAG = #{WXFLAG}
and s.DYFLAG = #{DYFLAG}
${SQLCONDITION}
order by s.SHOP_ID DESC

select sa.SHOPAPPROVE_ID, ss.SHOPSTATENAME, sa.APPROVECOMMENT, sa.CDT from shopapprove sa left join shopstate ss on sa.shopstate_id = ss.shopstate_id where 1 = 1
and sa.SHOP_ID = #{SHOP_ID}
order by sa.cdt desc

select s.SHOP_ID, s.MEMBER_ID, s.SHOPNAME, s.SHOPDESC, s.SHOPADDRESS, s.AVGMONEY, s.LONGITUDE, s.LATITUDE, s.CONTRACTPERSON, s.CONTRACTPHONE, s.ACCOUNTER, s.CDT, s.SHOPSTATE_ID, s.SHOPTYPE_ID, s.WAITACCOUNT, s.ALREADYACCOUNT, s.MARKET_ID, s.WXFLAG, s.DYFLAG, s.MEMBER_ID_DY, s.PERCENT, s.DISTANCE, ss.SHOPSTATENAME, st.SHOPTYPENAME, ifnull(f.FILEPATH,'shop.jpg') as FILEPATH, m.NICKNAME, m.PHOTO, m.WXOPEN_ID from ( SELECT SHOP_ID, MEMBER_ID, SHOPNAME, SHOPDESC, SHOPADDRESS, AVGMONEY, LONGITUDE, LATITUDE, CONTRACTPERSON, CONTRACTPHONE, ACCOUNTER, CDT, SHOPSTATE_ID, SHOPTYPE_ID, WAITACCOUNT, ALREADYACCOUNT, MARKET_ID, WXFLAG, DYFLAG, MEMBER_ID_DY, PERCENT, ROUND( 6378.138 * 2 * ASIN( SQRT( POW( SIN( (
latitude
#{LATITUDE}
* PI() / 180 - latitude * PI() / 180 ) / 2 ), 2 ) + COS(
latitude
#{LATITUDE}
* PI() / 180) * COS(latitude * PI() / 180) * POW( SIN( (
longitude
#{LONGITUDE}
* PI() / 180 - longitude * PI() / 180 ) / 2 ), 2 ) ) ) * 1000 ) AS DISTANCE FROM shop ) s left join shopstate ss on s.shopstate_id = ss.shopstate_id left join shoptype st on s.shoptype_id = st.shoptype_id left join file f on s.shop_id = f.reference_id and f.filetype = '7' left join member m on s.member_id = m.member_id where SHOP_ID = #{SHOP_ID}
