mapper.select:
select s.STAND_ID, s.GOODS_ID, s.STANDTYPE_ID, s.STANDDESC, s.CREATETIME, s.STATE, s.REMARK, st.STANDTYPENAME, g.GOODSNAME, sh.SHOP_ID, ifnull(f.FILEPATH,p.FILEPATH) as FILEPATH from s left join standtype st on s.standtype_id = st.standtype_id left join goods g on s.goods_id = g.goods_id left join ( SELECT * FROM file WHERE filetype = '1' GROUP BY reference_id ) p on g.goods_id = p.reference_id left join shop sh on g.shop_id = sh.shop_id left join file f on s.stand_id = f.reference_id and f.filetype = '3' where 1 = 1
and ( g.GOODSNAME LIKE CONCAT(CONCAT('%', #{pd.keywords}),'%') )
and s.STANDTYPE_ID = #{pd.STANDTYPE_ID}
and s.STATE = #{pd.STATE}
and sh.SHOP_ID = #{pd.SHOP_ID}
order by s.STAND_ID DESC

select s.STAND_ID, s.GOODS_ID, s.STANDTYPE_ID, s.STANDDESC, s.CREATETIME, s.STATE, s.REMARK, st.STANDTYPENAME, g.GOODSNAME, sh.SHOP_ID, ifnull(f.FILEPATH,p.FILEPATH) as FILEPATH from s left join standtype st on s.standtype_id = st.standtype_id left join goods g on s.goods_id = g.goods_id left join ( SELECT * FROM file WHERE filetype = '1' GROUP BY reference_id ) p on g.goods_id = p.reference_id left join shop sh on g.shop_id = sh.shop_id left join file f on s.stand_id = f.reference_id and f.filetype = '3' where 1 = 1
and ( g.GOODSNAME LIKE CONCAT(CONCAT('%', #{keywords}),'%') )
and s.STANDTYPE_ID = #{STANDTYPE_ID}
and s.STATE = #{STATE}
and sh.SHOP_ID = #{SHOP_ID}
order by s.STAND_ID DESC
